import React from 'react';

class SearchBar extends React.Component {
  state = { text: '' };

  render() {
    console.log(this.state.text);
    return (
      <form>
        <label>FORM!</label>
        <input
          value={this.state.text}
          onChange={(e) => this.setState({ text: e.target.value })}
        />
      </form>
    );
  }
}

export default SearchBar;
